mapper.insert:
insert into zt_sys_dataset (id, createTime, name, 
  describe, typeId, userId, 
  projectId, dataTable, rdbId
  )
values (#{id,jdbcType=VARCHAR}, #{createtime,jdbcType=TIMESTAMP}, #{name,jdbcType=VARCHAR}, 
  #{describe,jdbcType=VARCHAR}, #{typeid,jdbcType=VARCHAR}, #{userid,jdbcType=VARCHAR}, 
  #{projectid,jdbcType=VARCHAR}, #{datatable,jdbcType=VARCHAR}, #{rdbid,jdbcType=VARCHAR}
  )

mapper.select:
SELECT d.id, d.`name`, d.`describe` AS dateDesc, d.typeId, f.`describe` AS typeDesc, d.createTime FROM zt_sys_dataset d LEFT JOIN zt_sys_dataset_local_file f ON f.typeId = d.typeId where d.userId = #{userId}
and d.`name` like concat(concat('%',#{name}),'%')
ORDER BY d.createTime DESC
